// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import "./libraries/helpers/Roles.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";

contract Arbitrators is AccessControlUpgradeable {
    mapping (address => bool) public arbitrators;

    function addArbitrator(address _arbitrator) public onlyRole(Roles.ARBITRATOR_ROLE) {
        arbitrators[_arbitrator] = true;
    }

    function removeArbitrator(address _arbitrator) public onlyRole(Roles.ARBITRATOR_ROLE) {
        arbitrators[_arbitrator] = false;
    }

}

contracts/AssetManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import "./libraries/types/DataTypes.sol";
import "./libraries/helpers/Roles.sol";
import "./libraries/helpers/Events.sol";
import "./libraries/helpers/Errors.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";


contract AssetManager is AccessControlUpgradeable, ReentrancyGuardUpgradeable {
    mapping (address => mapping(address => uint)) public userBalances;
    mapping (address => DataTypes.UserAssetData) public userData;
    mapping (uint8 => address) public assets;
    mapping (address => bool) public isAssetApproved;

    uint8 public assetCount;

    function approveAsset(address _asset) public onlyRole(Roles.ASSET_MANAGER_ROLE) {
        assets[assetCount] = _asset;
        isAssetApproved[_asset] = true;
        assetCount++;

        emit Events.AssetApproved(_asset, msg.sender);
    }

    function removeAsset(address _asset) public onlyRole(Roles.ASSET_MANAGER_ROLE) {
        isAssetApproved[_asset] = false;

        emit Events.AssetRemoved(_asset, msg.sender);
    }

    function deposit(address _asset, uint _amount) public payable nonReentrant {

        if (isAssetApproved[_asset] == false) {

            revert Errors.ASSET_NOT_SUPPORTED();
        }

        if (_asset == address(0)) {

            userBalances[msg.sender][address(0)] += msg.value;


        } else {
            // requires that the user has approved the contract to spend the amount

            if (_amount == 0) {

                revert Errors.INVALID_AMOUNT();
            }

            IERC20Metadata(_asset).transferFrom(msg.sender, address(this), _amount);
            userBalances[msg.sender][_asset] += _amount;
        }

        emit Events.Deposit(msg.sender, _asset, _amount);
    }

    function userWithdraw(address _asset, uint _amount) public {

        if (isAssetApproved[_asset] == false) {

            revert Errors.ASSET_NOT_SUPPORTED();
        }

        if (_amount == 0) {

            revert Errors.INVALID_AMOUNT();
        }

        if(userBalances[msg.sender][_asset] < _amount) {

            revert Errors.INSUFFICIENT_BALANCE();
        }

        if (_asset == address(0)) {

            (bool success, ) = payable(msg.sender).call{value: _amount}("");
            if (!success) {

                revert Errors.TRANSFER_FAILED();
            }
        } else {

            IERC20Metadata(_asset).transfer(msg.sender, _amount);
        }
        userBalances[msg.sender][_asset] -= _amount;

        emit Events.Withdraw(msg.sender, _asset, _amount);
    }
}

contracts/FundsManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import "./libraries/helpers/Roles.sol";
import "./libraries/helpers/Events.sol";
import "./libraries/helpers/Errors.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";

contract FundsManager is AccessControlUpgradeable, ReentrancyGuardUpgradeable {

    function withdrawFunds(address payable _to, uint256 _amount) public onlyRole(Roles.FUNDS_MANAGER_ROLE) {
       (bool success, ) = _to.call{value: _amount}("");
       if (!success) {
           revert Errors.TRANSFER_FAILED();
       }

         emit Events.AdminWithdraw(_to, _amount);

    }

    fallback () external payable nonReentrant {

    }
}

contracts/GPCore.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

import "./libraries/helpers/Roles.sol";
import "./libraries/helpers/Errors.sol";
import "./libraries/helpers/Events.sol";
import "./OracleRegistry.sol";
import "./AssetManager.sol";
import "./FundsManager.sol";
import "./Rewards.sol";
import "./Arbitrators.sol";


/// @custom:security-contact jovi@gamepayy.com
contract GPCore is Initializable, PausableUpgradeable, AccessControlUpgradeable, UUPSUpgradeable, OracleRegistry, AssetManager, FundsManager, Arbitrators, Rewards {


    mapping(address => uint256) public balances;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize() initializer public {
        __Pausable_init();
        __AccessControl_init();
        __UUPSUpgradeable_init();

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(Roles.PAUSER_ROLE, msg.sender);
        _grantRole(Roles.UPGRADER_ROLE, msg.sender);
        _grantRole(Roles.FUNDS_MANAGER_ROLE, msg.sender);
        _grantRole(Roles.ASSET_MANAGER_ROLE, msg.sender);
        _grantRole(Roles.ARBITRATOR_ROLE, msg.sender);
        _grantRole(Roles.ORACLE_ADMIN_ROLE, msg.sender);
        _grantRole(Roles.REWARDS_ADMIN_ROLE, msg.sender);

        periodicity = 1 days;
        latestRewards = block.timestamp - 1 days;
    }

    function pause() public onlyRole(Roles.PAUSER_ROLE) {
        _pause();
    }

    function unpause() public onlyRole(Roles.PAUSER_ROLE) {
        _unpause();
    }

    function _authorizeUpgrade(address newImplementation)
        internal
        onlyRole(Roles.UPGRADER_ROLE)
        override
    {}

}

contracts/interfaces/IMulticall3.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.12 <0.9.0;

interface IMulticall3 {
    struct Call {
        address target;
        bytes callData;
    }

    struct Call3 {
        address target;
        bool allowFailure;
        bytes callData;
    }

    struct Call3Value {
        address target;
        bool allowFailure;
        uint256 value;
        bytes callData;
    }

    struct Result {
        bool success;
        bytes returnData;
    }

    function aggregate(Call[] calldata calls) external payable returns (uint256 blockNumber, bytes[] memory returnData);

    function aggregate3(Call3[] calldata calls) external payable returns (Result[] memory returnData);

    function aggregate3Value(Call3Value[] calldata calls) external payable returns (Result[] memory returnData);

    function blockAndAggregate(
        Call[] calldata calls
    ) external payable returns (uint256 blockNumber, bytes32 blockHash, Result[] memory returnData);

    function getBasefee() external view returns (uint256 basefee);

    function getBlockHash(uint256 blockNumber) external view returns (bytes32 blockHash);

    function getBlockNumber() external view returns (uint256 blockNumber);

    function getChainId() external view returns (uint256 chainid);

    function getCurrentBlockCoinbase() external view returns (address coinbase);

    function getCurrentBlockDifficulty() external view returns (uint256 difficulty);

    function getCurrentBlockGasLimit() external view returns (uint256 gaslimit);

    function getCurrentBlockTimestamp() external view returns (uint256 timestamp);

    function getEthBalance(address addr) external view returns (uint256 balance);

    function getLastBlockHash() external view returns (bytes32 blockHash);

    function tryAggregate(
        bool requireSuccess,
        Call[] calldata calls
    ) external payable returns (Result[] memory returnData);

    function tryBlockAndAggregate(
        bool requireSuccess,
        Call[] calldata calls
    ) external payable returns (uint256 blockNumber, bytes32 blockHash, Result[] memory returnData);
}

